def solution(string):
    length = len(string)
    minLength = length
    halfLength = minLength // 2
    i = 0
    while i < halfLength:
        previousString = ""
        duplicationCount = 0
        charCount = 0
        j = 0

        while j < length and charCount < minLength:
            currentString = string[j:(j + i + 1)]
            if previousString == currentString:
                duplicationCount += 1
            if previousString != currentString:
                if duplicationCount > 0:
                    charCount += getDigit(duplicationCount + 1)
                charCount += len(currentString)
                duplicationCount = 0

            previousString = currentString
            j += (i + 1)
        if duplicationCount > 0:
            charCount += getDigit(duplicationCount + 1)

        if charCount < minLength:
            minLength = charCount
        i += 1

    return minLength

def getDigit(number):
    count = 0
    temp = number
    while temp > 0:
        count += 1
        temp //= 10

    return count


print(solution('aabbaccc'))
print(solution('ababcdcdababcdcd'))
print(solution('abcabcdede'))
print(solution('abcabcabcabcdededededede'))
print(solution('xababcdcdababcdcd'))
print(solution(''))

